INSERT INTO `comment` VALUES ('5776', '1293359', ' 从没见过那么流氓的温柔，从没见过那么温柔的流氓。', '大头绿豆');
INSERT INTO `comment` VALUES ('5777', '1293359', ' 幽默，小伤感，公路电影，难得的好片子，大陆至今无法涉及并能拍的片子', '白羽');
