INSERT INTO `comment` VALUES ('6238', '2363506', ' 我还是喜欢那种仨傻子那种欢乐和鬼马的片儿啊，这个励志方面太满了，溢出来。', '琦殿');
INSERT INTO `comment` VALUES ('6239', '2363506', ' 天才和白痴只有一个伯乐的距离！', '心清如玉');
